def find_folder(root_folder, rex):
    # for root,dirs,files in tqdm_gui(os.walk(root_folder), total=):
    for root,dirs,files in os.walk(root_folder): # ,files
        for d in dirs:
            result = re.search(rex, d)
            if result:
                print(result)
                return os.path.join(root, d)

